import {useState, useEffect} from 'react'
import moviesUtils from '../utils/moviesUtils'
import AddMovieComp from './AddMovie'
import MovieComp from './Movie'

function AllMoviesComp() {

    const [movies, setMovies] = useState([])
    const [addMovie, setAddMovie] = useState(false)
    const [movie, setMovie] = useState({})
    
    useEffect(async() =>
    {
     let allMovies =await moviesUtils.allMovies();
     setMovies(allMovies);
    },)
  
    const addNewMovie= async()=>{
      let movieData= await moviesUtils.addMovie(movie);
      console.log(movieData)
    }


  return (
    <div className="App">

    <h1>Movies</h1>
    <button type="button" onClick={e => setAddMovie(!addMovie)}>Add Movie</button><br/><br/>

    <div>
      {addMovie&&
            <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
        
            Name : <input name="Name" required onChange={e => setMovie({...movie, Name : e.target.value})} /> <br/>
            Genres : <input name="Genres" required onChange={e => setMovie({...movie, Genres : e.target.value})} /><br/>
            Image URL : <input name="Image" required onChange={e => setMovie({...movie, Image : e.target.value})} /><br/>
            Premierd : <input type="Date" required name="Premierd" onChange={e => setMovie({...movie, Premierd : e.target.value})} /><br/>
      
            <input type="submit" value="Save" onClick={addNewMovie}/>
            <Link to="/movies">Cancel</Link>
      
      
      </div>
      }
  </div><br/><br/>
    {
      movies.map(item =>
        {
          return <div key={item._id}><MovieComp  movie={item} /><br/></div> 
        })
    }


    </div>
  );
}

export default AllMoviesComp;
