const appReducer= (state= {user: {}, permissions: []}, action) =>{

    switch(action.type){
        case "LOGIN" :
            return { ...state,  counter : state.counter + action.payload}

        case "LOGOUT" :
            return { ...state,  counter : state.counter - action.payload}
        default:
            return state;
    }
}

export default appReducer;
