import {useState} from 'react'
import loginUtlis from '../utils/loginUtils'
import { useDispatch } from "react-redux";
import Input from '@mui/material/Input';
import Button from '@mui/material/Button';
import {Link } from 'react-router-dom';

function CreateAccountComp(props) {

  const [userName, setUserName] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState('')

  const dispatch = useDispatch()

const checkUser=async() =>{

    let user= await loginUtlis.newUser(userName);
    console.log(user);
    
    if (user) {
      if (user.password.length == 0) {
        let Updateuser = await loginBL.Updateuser(user, password);
        if (Updateuser) {
          res.redirect('/login');
        }
        else {
          res.render("CreateAccount", { msg: "Update failed. please try again" })
        }
      }
      else {
        res.render("login", { msg: "User already exist!" })
      }
    }
    else {
      res.render("CreateAccount", { msg: "User not exist!" })
    }
}

  return (
    <div className="App">

      <h2>Create Account Page</h2>  <br/>
      <Input  placeholder="UserName"  type="text" name="Username" onChange={e => setUserName(e.target.value)} /> <br/>
      <Input  placeholder="Password" type="password" name="Password"  onChange={e => setPassword(e.target.value)}/>  <br/>
    <br/>
    <Button size="small" variant="outlined" onClick={checkUser}>Create</Button>
    <br/><br/>
   <Link to="/">Return to Login Page</Link>
    </div>
 
  );
}

export default CreateAccountComp;
