import {useState, useEffect} from 'react'
import usersUtils from '../utils/UsersUtils'
import UserComp from './User';

function AllUsersComp() {

  const [users, setUsers] = useState([])
  const [addUser, setAddUser] = useState(false)
  const [user, setUser] = useState({})
  const [isChecked, setisChecked]= useState(false)
  useEffect(async() =>
  {
   let allUsers =await usersUtils.allUsers();
   console.log(allUsers)
   setUsers(allUsers);
  },[])

 const CheckSub= (e) => {
     let Create, Update, Delete;
     if(e.target.id==="CreateSubscriptions"){
        let Create = e.target.checked;
        console.log(Create)
     }
     if(e.target.id==="DeleteSubscriptions"){
        let Delete = e.target.checked;
        console.log(Create)
     }
     if(e.target.id==="UpdateSubscriptions"){
        let Update = e.target.checked;
        console.log(Create)
     }
   
    if (Create ||Delete ||  Update) {
        setisChecked(true);
    } 
}
return (
  <div className="App">

  <h1>Users</h1>
  <button type="button" onClick={e => setAddUser(!addUser)}>Add User</button><br/><br/>

<div>
  {addUser&&
         <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
         Name : <input name="Name" required onChange={e => setUser({...user, Name : e.target.value})} /> <br/>
         User Name : <input name="userName" required onChange={e => setUser({...user, userName : e.target.value})} /><br/>
         Created Date : <input name="CreatedDate" required onChange={e => setUser({...user, CreatedDate : e.target.value})} /><br/>
         Session Time Out : <input name="SessionTimeOut" required onChange={e => setUser({...user, SessionTimeOut : e.target.value})} /><br/>
         Permissions : <br/>
         <input name="Permissions"  checked={isChecked} type="checkbox" value="View Subscriptions" id="ViewSubscriptions"/>
        <label for="ViewSubscriptions"> View Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Create Subscriptions" id="CreateSubscriptions" onClick ={CheckSub}/>
        <label for="CreateSubscriptions"> Create Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Delete Subscriptions" id="DeleteSubscriptions"  onClick ={CheckSub}/>
        <label for="DeleteSubscriptions"> Delete Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Update Subscriptions" id="UpdateSubscriptions" onClick ={CheckSub}/>
        <label for="UpdateSubscriptions"> Update Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="View Movies" id="ViewMovies"/>
        <label for="ViewMovies"> View Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Create Movies" id="CreateMovies" onClick = "CheckMovie()"/>
        <label for="CreateMovies"> Create Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Delete Movies" id="DeleteMovies" onClick = "CheckMovie()"/>
        <label for="DeleteMovies"> Delete Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Update Movies" id="UpdateMovies" onClick = "CheckMovie()"/>
        <label for="UpdateMovies">Update Movies</label><br/>

         {/* <input type="submit" value="Save" onClick={addNewMember}/> */}
         <button type="button" onClick={e =>setAddUser(!addUser)}>Cancel</button><br/><br/>
   </div>
  }
</div><br/><br/>
  {
    users.map(item =>
      {
        return <div key={item.id}><UserComp  user={item} /><br/></div> 
      })
  }


  </div>
);
}


export default AllUsersComp;
