import {useState, useEffect} from 'react'
import loginUtlis from '../utils/loginUtils'
import { useDispatch } from "react-redux";

function LoginComp(props) {

  const [userName, setUserName] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState('')
  const dispatch = useDispatch()
  const [state, setState] = useState({});

const checkUser=async() =>{

    let userData= await loginUtlis.checkUser(userName, password);
    if(userData){
      dispatch({type : "LOGIN" , payload : userData})
      props.history.push("/main");
    }
    if(userName==="" || password===""){
      setMsg("Please insert UserName & password")
    }
    else{
      setMsg("User name or password are wrong!")
    }
}

useEffect(() => {
  return () => {
    setState({}); // This worked for me
  };
}, []);



  return (
    <div className="App">

      <h2>Login Page</h2>  <br/>
      <label htmlFor="Username">Username:</label><br/>
      <input type="text" name="Username" onChange={e => setUserName(e.target.value)} /> <br/>
     <label htmlFor="Password">Password:</label>  <br/>
    <input type="password" name="Password"  onChange={e => setPassword(e.target.value)}/>  <br/>
    <br/>
    <input id="submit" type="submit" value="Login" onClick={checkUser}/>
    <br/><br/>
    {msg} <br/><br/>
    <p> New User? <a href="/CreateAccount">Create Account</a></p>

    </div>
 
  );
}

export default LoginComp;
