import {useState, useEffect} from 'react'
import memberUtils from '../utils/membersUtils'

const MemberComp = (props) =>
{
const [member, setMember] = useState({})
const [editMember, setEditMember] = useState(false)
const [UpdatedMember, setUpdatedMember] = useState({})

useEffect(async () =>
  {
    setMember(props.member);
    setUpdatedMember(props.member);
  },[props])

const deleteMember= async ()=>{
      let deletedMember= await memberUtils.deleteMember(props.member._id);
}

const updateMember= async()=>{
    let memberData= await memberUtils.EditMember(props.member._id, member);
    setMember(memberData)
    setEditMember(!editMember)
}

return(
<div style={{width:"300px", borderWidth : "5px", borderStyle : "solid", borderColor : "blue"}}>
        
        Name : {member.Name} <br/> 
        Email : {member.Email} <br/>
        City : {member.City} <br/>
        <input type="button" value="Edit"/>
        <input type="button" value="Delete" onClick={deleteMember}/><br/><br/>

        <button type="button" onClick={e => setEditMember(!editMember)}>Edit</button><br/><br/>

<div>
  {editMember&&
         <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
         Name : <input name="Name" onChange={e => setMember({...member, Name : e.target.value})} /> <br/>
         Email : <input name="Email" value={member.Email} onChange={e => setMember({...member, Email : e.target.value})} /><br/>
         City : <input name="City" value={member.City} onChange={e => setMember({...member, City : e.target.value})} /><br/>
         <input type="submit" value="Save" onClick={updateMember}/>
         <button type="button" onClick={e => setEditMember(!editMember)}>Cancel</button><br/><br/>
   </div>
  }
</div>
</div>

)
}



export default MemberComp;
