import axios from 'axios'

const checkUser = async(userName, password) =>
{
    let resp = await axios.get("http://localhost:8000/api/users");
    let users = resp.data;
console.log(users)
    let user= users.filter(x=> x.userName===userName && x.password===password)
    return user;
}

const getItem = (id, url) =>
{
    return axios.get(url + "/" + id)
}

const addItem = (url, obj) =>
{
    return axios.post(url,obj)
}

const updateItem = (url,id, obj) =>
{
    return axios.put(url + "/ " + id,obj)
}

const deleteItem = (url,id) =>
{
    return axios.delete(url + "/ " + id)
}





export default  {checkUser,getItem,addItem,updateItem,deleteItem}
