import { useState, useEffect } from 'react'
import memberUtils from '../utils/membersUtils'
import { useSelector } from "react-redux";
import subscriptionsUtils from '../utils/subscriptionsUtils'
import moviesUtils from '../utils/moviesUtils'
import { Link } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const MemberComp = (props) => {
  const [member, setMember] = useState({})
  const [movie, setMovie] = useState({})
  const [movies, setMovies] = useState([])
  const [watchedMovies, setWatchedMovies] = useState([])
  const [editMember, setEditMember] = useState(false)
  const [subscribe, setSubscribe] = useState(false)
  const [updatedMember, setUpdatedMember] = useState({})
  const permissions = useSelector((state) => state.user.Permissions)

  useEffect(() => {
    async function fetchData() {
    
      if (props.member) {
        setMember(props.member);
      }
      else {
        let member = await memberUtils.getMember(props.match.params.id);
        setMember(member)
      }
    }
    fetchData();
  }, [props])

  useEffect(() => {
    async function fetchData() {
      let allMovies = await moviesUtils.allMovies();
      setMovies(allMovies)

      let id=props.member? props.member._id : props.match.params.id
     let MoviesWatched= await subscriptionsUtils.getSubscriptionsByMember(id);
     setWatchedMovies(MoviesWatched)}
      fetchData();
    },[])
  

  useEffect(() => {
    async function fetchData() {
      if (props.member) {
        setMember(props.member);
      }
      else {
        let member = await memberUtils.getMember(props.match.params.id);
        setMember(member)
      }
    }
    fetchData();
  }, [props])

  const deleteMember = async () => {
    let id=props.member? props.member._id : props.match.params.id
    return await memberUtils.deleteMember(id);
  }

  const updateMember = async () => {
    await memberUtils.EditMember(props.member._id, updatedMember);
    setEditMember(!editMember)
  }

  const Subscribe = async () => {
    let subscription = {
      MemberId: member._id,
      Movies: { "movieId": movie.id, "date": movie.date }
    }
    await subscriptionsUtils.AddSubscriptions(subscription);
    setSubscribe(!subscribe)
  }

  const getMovieName=(id)=>{
    let movieData= movies.filter(x=>x._id===id);
    return movieData[0].Name;
  }

  return (
    <Card >

    <CardContent>
      <div style={{ width: "400px", borderWidth: "3px", borderStyle: "solid", borderColor: "gray" }}>
        <Typography gutterBottom  component="div">
        Name : {member.Name} <br />
      Email : {member.Email} <br />
      City : {member.City} <br />
        </Typography>


      <div style={{ width: "350px", borderWidth: "2px", borderStyle: "solid", borderColor: "blue" }}>
        <h4> Movies Watched : </h4>
        {watchedMovies.length >0 &&
     <ul>
       {watchedMovies.map((item)=>{
         return <li key={item._id}>
          <Link to={"/movie/" + item.Movies.movieId} >
          {getMovieName(item.Movies.movieId)}
            </Link>,
           {item.Movies.date} </li>
       })}
       
     </ul>
}
<Button variant="contained" color="success" value="Subscribe to new movie" onClick={e => setSubscribe(!subscribe)}/>

        {subscribe &&
        <div style={{ width: "300px", borderWidth: "3px", borderStyle: "solid", borderColor: "black" }}>

          Movie :

          <select name="movie" id="movie" required onChange={e => setMovie({...movie, id : e.target.value})}>
            { movies.map((item) => {
              return  <option key={item._id} value={item._id} >{item.Name} </option>
            })}
          </select><br/>

          Date : <input type="date" name="Date" required onChange={e => setMovie({ ...movie, date: e.target.value })} /><br />

          <input type="submit" value="Subscribe" onClick={Subscribe} />
          <button type="button" onClick={e => setSubscribe(!subscribe)}>Cancel</button><br /><br />
        </div>
      }
      </div>
   
  <CardActions>
      {permissions.includes("Update Subscriptions") &&
        <div>
           <Button size="small" variant="outlined" onClick={e => setEditMember(!editMember)}>Edit</Button>
        </div>
      }
      {permissions.includes("Delete Subscriptions") &&
        <div>
       <Button  size="small" variant="outlined" color="error" onClick={deleteMember}>Delete</Button>
        </div>
      }
      </CardActions>
      <div>
        {editMember &&
          <div style={{ width: "300px", borderWidth: "3px", borderStyle: "solid", borderColor: "gray" }}>
            Name : <input name="Name" value={member.Name} onChange={e => setUpdatedMember({ ...updatedMember, Name: e.target.value })} /> <br />
            Email : <input name="Email" value={member.Email} onChange={e => setUpdatedMember({ ...updatedMember, Email: e.target.value })} /><br />
            City : <input name="City" value={member.City} onChange={e => setUpdatedMember({ ...updatedMember, City: e.target.value })} /><br />
            <input type="submit" value="Save" onClick={updateMember} />
            <button type="button" onClick={e => setEditMember(!editMember)}>Cancel</button>
          </div>
        }
      </div>
    </div>
    </CardContent>
    </Card>
  )
}



export default MemberComp;
