import {useState, useEffect} from 'react'
import UsersUtils from '../utils/UsersUtils'

const UserComp = (props) =>
{
const [user, setUser] = useState({})
const [permissions, setPermissions] = useState(props.user.Permissions)
const [editUser, setEditUser] = useState(false)
const [disable, setdisable] = useState(true)
const [updatedUser, setUpdatedUser] = useState({})

useEffect(() => {
  async function fetchData() {
      setUser(props.user);}
      fetchData();
    },[props])
  
const deleteUser= async ()=>{
     return await UsersUtils.deleteUser(props.user.id);
}

const updateUser= async()=>{
    let userData= await UsersUtils.EditUser(props.user.id, user);
    setEditUser(!editUser)
}

return(


<div style={{width:"300px", borderWidth : "5px", borderStyle : "solid", borderColor : "blue"}}>
    First Name : <input name="FName" value={user.FirstName} disabled={disable} required onChange={e => setUser({...user, FirstName : e.target.value})} /> <br/>
    Last Name : <input name="LName" value={user.LastName} disabled={disable} required onChange={e => setUser({...user, LastName : e.target.value})} /> <br/>
    User Name : <input name="userName" value={user.userName} disabled={disable} required onChange={e => setUser({...user, userName : e.target.value})} /><br/>
    Created Date : <input disabled name="CreatedDate"  value={user.CreatedDate}/><br/>
    Session Time Out : <input name="SessionTimeOut"  value={user.SessionTimeOut} disabled={disable}  required onChange={e => setUser({...user, SessionTimeOut : e.target.value})} /><br/>
    Permissions : <br/>
          {
            permissions.map((item, index)=>{
              return(
              <div  key={index}>
                <input  disabled={disable} name="Permissions" type="checkbox" value={item} />
              <label> {item}</label>
              </div>)
            })
          }

      {!disable &&
          <div>
            <input type="button" value="Update User" onClick={updateUser}/><br/><br/>
         </div>
        }

        <input type="button" value="Edit" type="button" onClick={e => setdisable(!disable)}/>
        <input type="button" value="Delete" onClick={deleteUser}/><br/><br/>

</div>

)
}



export default UserComp;
