import axios from 'axios'

const allMovies = async() =>
{
    let resp = await axios.get("http://localhost:8000/api/movies");
    let movies = resp.data;
    return movies;
}

const deleteMovie = async(id) =>
{
    let resp = await axios.get("http://localhost:8000/api/movies/deleteMovie/" + id);
    let movie = resp.data;
    return movie;
}

const addMovie = async(obj) =>
{
    let resp = await axios.get("http://localhost:8000/api/movies/addMovie" ,obj);
    let movie = resp.data;
    return movie;
}


export default  {allMovies, deleteMovie, addMovie}
