import {useState} from 'react'
import loginUtlis from '../utils/loginUtils'
import { useDispatch } from "react-redux";
import MainComp from './views/main';
import {Switch, Route, Link} from 'react-router-dom';
import AllMembersComp from './views/AllMembers';
import AllMoviesComp from './views/AllMovies';
import AllUsersComp from './views/UsersManagement';

function LoginComp(props) {

  const [userName, setUserName] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState('')
  const dispatch = useDispatch()

const checkUser=async() =>{

    let userData= await loginUtlis.checkUser(userName, password);
    if(userData){
      dispatch({type : "LOGIN" , payload : userData})
      props.history.push("/main/" + userData.id)
    }
    if(userName==="" || password===""){
      setMsg("Please insert UserName & password")
    }
    else{
      setMsg("User name or password are wrong!")
    }
}


  return (
    <div className="App">

      <h2>Login Page</h2>  <br/>
      <label htmlFor="Username">Username:</label><br/>
      <input type="text" name="Username" onChange={e => setUserName(e.target.value)} /> <br/>
     <label htmlFor="Password">Password:</label>  <br/>
    <input type="password" name="Password"  onChange={e => setPassword(e.target.value)}/>  <br/>
    <br/>
    <input id="submit" type="submit" value="Login" onClick={checkUser}/>
    <br/><br/>
    {msg} <br/><br/>
    <p> New User? <a href="/CreateAccount">Create Account</a></p>

    <Switch>
            <Route exact path="/" component={LoginComp} />
            <Route path="/login" component={LoginComp} />
            <Route path="/main" component={MainComp} />
            <Route path="/movies" component={AllMoviesComp} />
            <Route path="/members" component={AllMembersComp} />
            <Route path="/users" component={AllUsersComp} />
        </Switch>


    </div>
 
  );
}

export default LoginComp;
