import {useState, useEffect} from 'react'
import usersUtils from '../utils/UsersUtils'
import UserComp from './User';

function AllUsersComp() {

  const [users, setUsers] = useState([])
  const [addUser, setAddUser] = useState(false)
  const [user, setUser] = useState({})
  const [date, setdate] = useState('')
  const [SubscriptionsPer, setSubscriptionsPer]= useState({CreateSubscriptions: false, DeleteSubscriptions: false , UpdateSubscriptions: false})
  const[SubscriptionsPerisChecked, setSubscriptionsPerisChecked]= useState(false)
  const [MoviesPer, setMoviesPer]= useState({CreateMovies: false, DeleteMovies: false , UpdateMovies: false})
  const[MoviesPerisChecked, setMoviesPerisChecked]= useState(false)
  const [permissions, setPermissions] = useState([])

  useEffect(async() =>
  {
   let allUsers =await usersUtils.allUsers();
   let someDate = new Date();
   let numberOfDaysToAdd = 3;
   let today = someDate.setDate(someDate.getDate() + numberOfDaysToAdd); 
   setdate(today);
   setUsers(allUsers);
  },[])

  useEffect(async() =>
  {
   if(SubscriptionsPer.CreateSubscriptions || SubscriptionsPer.DeleteSubscriptions || SubscriptionsPer.UpdateSubscriptions){
    setSubscriptionsPerisChecked(true)
   }
   if(!SubscriptionsPer.CreateSubscriptions && !SubscriptionsPer.DeleteSubscriptions && !SubscriptionsPer.UpdateSubscriptions){
    setSubscriptionsPerisChecked(false)
   }
  },[SubscriptionsPer])

  useEffect(async() =>
  {
   if(MoviesPer.CreateMovies || MoviesPer.DeleteMovies || MoviesPer.UpdateMovies){
    setMoviesPerisChecked(true)
   }
   if(!MoviesPer.CreateMovies && !MoviesPer.DeleteMovies && !MoviesPer.UpdateMovies){
    setMoviesPerisChecked(false)
   }
  },[MoviesPer])

//Create permissions array
  const addPermissions=(e) =>{
    let per= permissions;
    console.log(e.target.value)
    if (e.target.checked) {
       per.push(e.target.value)
    } 
    if (!e.target.checked) {
       let index = per.indexOf(e.target.value)
       per.splice(index, 1)
     } 

     if(SubscriptionsPerisChecked){
         per.push("View Subscriptions")
     }
     else if(!SubscriptionsPerisChecked){
        let index = per.indexOf("View Subscriptions")
        per.splice(index, 1)
     }
     if(MoviesPerisChecked){
        per.push("View Movies")
    }
    else if(!MoviesPerisChecked){
        let index = per.indexOf("View Movies")
        per.splice(index, 1)
     }
     setPermissions(per)
  }

  const addNewUser= async()=>{
    let userData= await usersUtils.addUser(user);
    setAddUser(!addUser)
  }

return (
  <div className="App">

  <h1>Users</h1>
  <button type="button" onClick={e => setAddUser(!addUser)}>Add User</button><br/><br/>

<div>
  {addUser&&
         <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
         Name : <input name="Name" required onChange={e => setUser({...user, Name : e.target.value})} /> <br/>
         User Name : <input name="userName" required onChange={e => setUser({...user, userName : e.target.value})} /><br/>
         Created Date : <input type="date" name="CreatedDate" value={date}/><br/>
         Session Time Out : <input name="SessionTimeOut" required onChange={e => setUser({...user, SessionTimeOut : e.target.value})} /><br/>
         Permissions : <br/>
         <input name="Permissions" checked={SubscriptionsPerisChecked} onChange={() => setSubscriptionsPerisChecked(!SubscriptionsPerisChecked)}  type="checkbox" value="View Subscriptions" id="ViewSubscriptions"/>
        <label htmlFor="ViewSubscriptions"> View Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Create Subscriptions" id="CreateSubscriptions" onChange={addPermissions}  onClick={e => setSubscriptionsPer({...SubscriptionsPer, CreateSubscriptions : !SubscriptionsPer.CreateSubscriptions})}/>
        <label htmlFor="CreateSubscriptions"> Create Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Delete Subscriptions" id="DeleteSubscriptions" onChange={addPermissions} onClick={e => setSubscriptionsPer({...SubscriptionsPer, DeleteSubscriptions : !SubscriptionsPer.DeleteSubscriptions})}/>
        <label htmlFor="DeleteSubscriptions"> Delete Subscriptions</label><br/>
        <input name="Permissions" type="checkbox" value="Update Subscriptions" id="UpdateSubscriptions"  onChange={addPermissions} onClick={e => setSubscriptionsPer({...SubscriptionsPer, UpdateSubscriptions : !SubscriptionsPer.UpdateSubscriptions})}/>
        <label htmlFor="UpdateSubscriptions"> Update Subscriptions</label><br/>
        <input name="Permissions" checked={MoviesPerisChecked} onChange={() => setMoviesPerisChecked(!MoviesPerisChecked)} type="checkbox" value="View Movies" id="ViewMovies"/>
        <label htmlFor="ViewMovies"> View Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Create Movies" id="CreateMovies"  onChange={addPermissions} onClick={e => setMoviesPer({...MoviesPer, CreateMovies : !MoviesPer.CreateMovies})}/>
        <label htmlFor="CreateMovies"> Create Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Delete Movies" id="DeleteMovies" onChange={addPermissions} onClick={e => setMoviesPer({...MoviesPer, DeleteMovies : !MoviesPer.DeleteMovies})}/>
        <label htmlFor="DeleteMovies"> Delete Movies</label><br/>
        <input name="Permissions" type="checkbox" value="Update Movies" id="UpdateMovies" onChange={addPermissions} onClick={e => setMoviesPer({...MoviesPer, UpdateMovies : !MoviesPer.UpdateMovies})}/>
        <label htmlFor="UpdateMovies">Update Movies</label><br/>

         <input type="submit" value="Save" onClick={addNewUser}/>
         <button type="button" onClick={e =>setAddUser(!addUser)}>Cancel</button><br/><br/>
   </div>
  }
</div><br/><br/>
  {
    users.map(item =>
      {
        return <div key={item.id}><UserComp  user={item} /><br/></div> 
      })
  }


  </div>
);
}


export default AllUsersComp;
