const appReducer= (state= {user: {}, members: [], movies: []}, action) =>{

    switch(action.type){
       
        case "LOGIN" :
            return { ...state,  user : action.payload}

        case "LOGOUT" :
            return { ...state,  user : {}}
        case "MEMBERS" :
            return { ...state,  members : action.payload}
            case "MOVIES" :
            return { ...state,  movies : action.payload}
        default:
            return state;
    }
}

export default appReducer;
