import {useState, useEffect} from 'react'
import UsersUtils from '../utils/UsersUtils'

const UserComp = (props) =>
{
const [user, setUser] = useState({})
const [permissions, setPermissions] = useState([])
const [editUser, setEditUser] = useState(false)
const [updatedUser, setUpdatedUser] = useState({})

useEffect(async () =>
  {
    setUser(props.user);
    setPermissions(props.user.Permissions)
  },[props])

const deleteUser= async ()=>{
     return await UsersUtils.deleteUser(props.user.id);
}

const updateUser= async()=>{
    let userData= await UsersUtils.EditUser(props.user.id, updatedUser);
    setEditUser(!editUser)
}

return(


<div style={{width:"300px", borderWidth : "5px", borderStyle : "solid", borderColor : "blue"}}>
        
         Name : {user.Name} <br/> 
        User Name : {user.userName} <br/>
        Created Date :{user.CreatedDate}<br/>
        Session Time Out : {user.SessionTimeOut}<br/>
        Permissions :
        <ul>
          {
            permissions.map((item, index)=>{
              return <li key={index}>{item}</li>
            })
          }
        </ul>
        
        <input type="button" value="Edit" type="button" onClick={e => setEditUser(!editUser)}/>
        <input type="button" value="Delete" onClick={deleteUser}/><br/><br/>
    <div>
      {editUser&&
            <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
            First Name : <input name="FName" required onChange={e => setUpdatedUser({...updatedUser, FirstName : e.target.value})} /> <br/>
         Last Name : <input name="LName" required onChange={e => setUpdatedUser({...updatedUser, LastName : e.target.value})} /> <br/>
         User Name : <input name="userName" required onChange={e => setUpdatedUser({...updatedUser, userName : e.target.value})} /><br/>
         Created Date : <input disabled name="CreatedDate" value={date}/><br/>
         Session Time Out : <input name="SessionTimeOut" required onChange={e => setUpdatedUser({...updatedUser, SessionTimeOut : e.target.value})} /><br/>
         Permissions : <br/>
        
            <input type="submit" value="Save" onClick={updateUser}/>
            <button type="button" onClick={e => setEditUser(!editUser)}>Cancel</button>
      </div>
      }
    </div>
</div>

)
}



export default UserComp;
