import {useState, useEffect} from 'react'
import moviesUtils from '../utils/moviesUtils'

const MovieComp = (props) =>
{
const [movie, setMovie] = useState({})
const [editMovie, setEditMovie] = useState(false)
const [updatedMovie, setUpdatedMovie] = useState({})

useEffect(async () =>
  {
    setMovie(props.movie);
  },[props])

const deleteMovie= async ()=>{
    return await moviesUtils.deleteMovie(props.movie._id);
}
const updateMovie= async()=>{
  let MovieData= await moviesUtils.EditMovie(props.movie._id, updatedMovie);
  setEditMovie(!editMovie)
}
return(
<div style={{width:"300px", borderWidth : "5px", borderStyle : "solid", borderColor : "blue"}}>
        
      Name : {movie.Name} <br/> <br/>

      <img width = "140px" height = "200px" src={movie.Image} /><br/>
      <input type="button" value="Edit" type="button" onClick={e => setEditMovie(!editMovie)}/>
      <input type="button" value="Delete" onClick={deleteMovie}/><br/><br/>
      <div>
      {editMember&&
            <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
            Name : <input name="Name" value={movie.Name} onChange={e => setUpdatedMovie({...updatedMovie, Name : e.target.value})} /> <br/>
            Genres : <input name=" Genres" value={movie.Genres}  onChange={e => setUpdatedMovie({...updatedMovie,  Genres : e.target.value})} /><br/>
            Image URL : <input name="Image" value={movie.Image}   onChange={e => setUpdatedMovie({...updatedMovie, Image : e.target.value})} /><br/>
            Premierd : <input type="Date" name="Premierd" onChange={e => setUpdatedMovie({...updatedMovie, Premierd : e.target.value})} /><br/>

            <input type="submit" value="Save" onClick={updateMovie}/>
            <button type="button" onClick={e => setEditMovie(!editMovie)}>Cancel</button>
      </div>
      }
    </div>
</div>)
}



export default MovieComp;
