import {Switch, Route, Link} from 'react-router-dom';
import AllMembersComp from './AllMembers';
import AllMoviesComp from './AllMovies';
import LoginComp from './login';
import {useEffect} from 'react'
import axios from 'axios'
import AllUsersComp from './UsersManagement';
import usersUtils from '../utils/UsersUtils'
import {useState} from 'react'

function MainComp(props) {

  const [Role, setRole] = useState('')
  const [permissions, setPermissions] = useState([])

  useEffect(() => {
    async function fetchData() {
      let data= await axios.get("http://localhost:8000/api/main");   
       let allUsers =await usersUtils.allUsers();
       let user= allUsers.filter(x=> x.id===props.match.params.id)
       if(user.userName==="admin"){
        setRole("admin")
       }
       setPermissions(user.Permissions);
    }
    fetchData();
  }, []); 


    return (
      <div className="App">
        <h1>Movies - Subscriptions Web Site </h1>
        {permissions.includes("View Movies") &&
         <Link to="/movies">All Movies</Link>
        }
         <Link to="/members">All Memebers</Link>
         {Role==="admin" &&
          <Link to="/users">Users Management</Link>
        }
  
         <Link to="/login">Log Out</Link>

        <Switch>
            <Route path="/login" component={LoginComp} />
            <Route path="/movies" component={AllMoviesComp} />
            <Route path="/members" component={AllMembersComp} />
            <Route path="/users" component={AllUsersComp} />
        </Switch>
      </div>
    );
  }
  
  export default MainComp;
