import {useState, useEffect} from 'react'
import UsersUtils from '../utils/UsersUtils'
import { useSelector } from "react-redux";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import Input from '@mui/material/Input';

const UserComp = (props) =>
{
const [user, setUser] = useState({})
const [editUser, setEditUser] = useState(false)
const [disable, setdisable] = useState(true)
const storeData = useSelector(state => state)
const [permissions, setPermissions] = useState([])

useEffect(() => {
  async function fetchData() {
      setUser(props.user);}
      fetchData();
    },[props])

useEffect(() => {
  async function fetchData() {
    setPermissions(storeData.user.Permissions)}
    fetchData();
},[storeData.user.Permissions])
const deleteUser= async ()=>{
     return await UsersUtils.deleteUser(props.user.id);
}

const updateUser= async()=>{
    await UsersUtils.EditUser(props.user.id, user);
    setEditUser(!editUser)
}

return(

  <Card >

  <CardContent>
    <div style={{ width: "400px", borderWidth: "3px", borderStyle: "solid", borderColor: "gray" }}>
    First Name : <Input id="component-outlined" name="FName" value={user.FirstName} disabled={disable} required onChange={e => setUser({...user, FirstName : e.target.value})} /> <br/>
    Last Name : <Input name="LName" value={user.LastName} disabled={disable} required onChange={e => setUser({...user, LastName : e.target.value})} /> <br/>
    User Name :  <Input name="userName" value={user.userName} disabled={disable} required onChange={e => setUser({...user, userName : e.target.value})} /><br/>
    Created Date :  <Input disabled name="CreatedDate"  value={user.CreatedDate}/><br/>
    Permissions : <br/>
          {
            permissions.map((item, index)=>{
              return(
              <div  key={index}>
                <Checkbox sx={{ '& .MuiSvgIcon-root': { fontSize: 20 } }} disabled={disable} name="Permissions" value={item} />
              <label> {item}</label>
              </div>)
            })
          }

      {!disable &&
          <div>
            <input type="button" value="Update User" onClick={updateUser}/><br/><br/>
         </div>
        }

        <input type="button" value="Edit" onClick={e => setdisable(!disable)}/>
        <input type="button" value="Delete" onClick={deleteUser}/><br/><br/>

</div>
</CardContent>
    </Card>
)
}



export default UserComp;
