const appReducer= (state= {user: {}, movies: []}, action) =>{
    switch(action.type){
       
        case "LOGIN" :
            return { ...state,  user : action.payload}

        case "LOGOUT" :
            return { ...state,  user : {}}

        case "ALLMOVIES" :
            return { ...state,  movies : action.payload}
 
        default:
            return state;
    }
}

export default appReducer;
