  const [member, setMember] = useState({})

  useEffect(async() =>
  {
   let allMembers =await memberUtils.allMembers();
   setMemebers(allMembers);
  },)

  const addNewMember= async()=>{
    let memberData= await memberUtils.addMember(member);
    console.log(memberData)
  }
  
return (
  <div className="App">

  <h1>Members</h1>
  <button type="button" onClick={e => setAddMember(!addMember)}>Add Member</button><br/><br/>

<div>
  {addMember&&
