import { Switch, Route, Link } from 'react-router-dom';
import { useState } from 'react'
import { useSelector } from "react-redux";
import { useEffect } from 'react'
import axios from 'axios'
import LoginComp from './login';
import AllMembersComp from './AllMembers';
import AllMoviesComp from './AllMovies';
import AllUsersComp from './UsersManagement';
import { useDispatch } from "react-redux";
import memberUtils from '../utils/membersUtils'
import moviesUtils from '../utils/moviesUtils'

function MainComp() {

  const [user, setUser] = useState({})
  const [Role, setRole] = useState('')
  const [permissions, setPermissions] = useState([])
  const dispatch = useDispatch()
  const storeData = useSelector(state => state)

  useEffect(() => {
    async function fetchData() {
      let allMembers =await memberUtils.allMembers();
      dispatch({type : "MEMBERS" , payload :allMembers})

      let allMovies =await moviesUtils.allMovies();
      dispatch({type : "MOVIES" , payload :allMovies})
    }
    fetchData();
  }, );

  useEffect(() => {
    async function fetchData() {
    setUser(storeData.user)
    setPermissions(storeData.user.Permissions)
    setRole(storeData.user.Role)}
    fetchData();
  },[storeData]);

  const logout=async() =>{
      dispatch({type : "LOGOUT" , payload : {}})
}


  return (
    <div className="App">
      <h1>Movies - Subscriptions Web Site </h1>
      Welcome {user.userName} <br/>
      {permissions &&
        <div>
          {
            permissions.includes("View Movies") &&
           <button><Link to="/movies">All Movies</Link></button> 
          }
          {permissions.includes("View Subscriptions") &&
             <button><Link to="/members">All Memebers</Link></button>}

          {Role === "admin" &&
           <button><Link to="/users">Users Management</Link></button> 
          }
         <button><Link to="/" onClick={logout}>Log Out</Link></button> 
        </div>
      }

    <Switch>
            <Route exact path="/" component={LoginComp} />
            <Route path="/login" component={LoginComp} />
            <Route path="/movies" component={AllMoviesComp} />
            <Route path="/members" component={AllMembersComp} />
            <Route path="/users" component={AllUsersComp} />
        </Switch>
    </div>
  );
}

export default MainComp;
