import {useState, useEffect} from 'react'
import moviesUtils from '../utils/moviesUtils'
import subscriptionsUtils from '../utils/subscriptionsUtils'
import { useSelector } from "react-redux";
import { Link } from 'react-router-dom';

const MovieComp = (props) =>
{
const [movie, setMovie] = useState({})
const [subscriptions, setSubscriptions] =  useState([])
const [editMovie, setEditMovie] = useState(false)
const [updatedMovie, setUpdatedMovie] = useState({})
const permissions= useSelector((state) => state.user.Permissions)

useEffect(() => {
  async function fetchData() {
    let id=props.movie? props.movie._id : props.match.params.id
   let subscriptionsData= await subscriptionsUtils.getSubscriptionsByMovie(id);
   setSubscriptions(subscriptionsData)}
    fetchData();
  },[])

useEffect(() => {
  async function fetchData() {
    if(props.movie){
      setMovie(props.movie)
    }
  else{
    let movie = await moviesUtils.getMovie(props.match.params.id);
    setMovie(movie)
  }
  ;}
    fetchData();
  },[props])

const deleteMovie= async ()=>{
    return await moviesUtils.deleteMovie(movie._id);
}

const sendData = async (e) =>
{
  e.preventDefault();
  let MovieData= await moviesUtils.EditMovie(movie._id, updatedMovie);
  setEditMovie(!editMovie)
  return MovieData
}

return(
<div style={{width:"300px", borderWidth : "5px", borderStyle : "solid", borderColor : "blue"}}>
        
     <h3> {movie.Name},   {movie.Premiered}</h3>
      Genres:  {movie.Genres} <br/> <br/>
      <img  alt={movie.id} width = "140px" height = "200px" src={movie.Image} /><br/>

      <div>
      <h4> Subscriptions Watched : </h4>
      {subscriptions.length >0 &&
     <ul>
       {subscriptions.map((item, index)=>{
         return <li key={index}>
          <Link to={"/member/" + item.MemberId} >{item.MemberId}</Link>,
           {item.Movies.date} </li>
       })}
     </ul>
}
      </div>
      {permissions.includes("Update Movies") &&
    <div>
      <input type="button" value="Edit" onClick={e => setEditMovie(!editMovie)}/>
    </div>
    }
    {permissions.includes("Delete Movies") &&
    <div>
      <input type="button" value="Delete" onClick={deleteMovie}/><br/><br/>
    </div>
    }
      <div>
      {editMovie&&
            <div style={{width:"300px", borderWidth : "3px", borderStyle : "solid", borderColor : "black"}}>
           <form onSubmit={e => sendData(e)}>
            Name : <input name="Name" value={updatedMovie.Name} onChange={e => setUpdatedMovie({...updatedMovie, Name : e.target.value})} /> <br/>
            Genres : <input name=" Genres" value={updatedMovie.Genres}  onChange={e => setUpdatedMovie({...updatedMovie,  Genres : e.target.value})} /><br/>
            Image URL : <input name="Image" value={updatedMovie.Image}   onChange={e => setUpdatedMovie({...updatedMovie, Image : e.target.value})} /><br/>
            Premiered : <input type="date" name="Premiered" onChange={e => setUpdatedMovie({...updatedMovie, Premiered : e.target.value})} /><br/>

            <input type="submit" value="Save"/>
            <button type="button" onClick={e => setEditMovie(!editMovie)}>Cancel</button>
            </form> 
      </div>
      }
    </div>
</div>)
}



export default MovieComp;
